import React, { Component } from 'react';
import SendbirdChat from '@sendbird/chat';
import { OpenChannelModule } from '@sendbird/chat/openChannel';
import { GroupChannelModule } from '@sendbird/chat/groupChannel';
import Title from './Title';
import MessageList from './MessageList';
import SendMessageForm from './SendMessageForm';

class App extends Component {
  constructor() {
    super();
    this.state = {
      messages: [],
      channelType: null,
      userId: '', // To be inputted by the user
      isConnected: false,
      messages: [],
      messageCollection: null,  
    };
    this.sb = SendbirdChat.init({
      appId: '24C7F7B1-B8F1-43FF-96BE-797B57528110',
      modules: [new OpenChannelModule(), new GroupChannelModule()],
    });
  }

  handleUserIdChange = (event) => {
    this.setState({ userId: event.target.value });
  };

  handleLogin = async () => {
    const { userId } = this.state;
    if (userId.trim()) {
      try {
        await this.sb.connect(userId);
        this.setState({ isConnected: true });
      } catch (error) {
        console.error('Sendbird Connection Error:', error);
      }
    }
  };

  connectToSenbird = async (userId) => {
    try {
      await this.sb.connect(userId);
      // ... after successful connection
    } catch (error) {
      console.error('Sendbird Connection Error:', error);
    }
  };

  createOpenChannel = async () => {
    const params = {
      name: "My Open Channel",
      coverUrlOrImage: "http://example.com/cover.jpg",
      operatorUserIds: ["wonbae"],
      data: "Additional information about the channel",
      customType: "my_custom_type"
    };

    try {
      const channel = await this.sb.openChannel.createChannel(params);
      console.log("Open channel created:", channel);
      await channel.enter();
      this.channel = channel; // Set the current channel context
    } catch (error) {
      console.error("Error creating or entering open channel:", error);
    }
  };

  createGroupChannel = async () => {
    const params = {
      invitedUserIds: ['min','wonbae'], 
      name: "My Group Channel",
      coverUrl: "http://example.com/cover.jpg",
      operatorUserIds: ['wonbae'], 
      isDistinct: true,
    };

    try {
      const channel = await this.sb.groupChannel.createChannel(params);
      console.log("Group channel created:", channel);
      this.channel = channel; // Set the current channel context
      console.log("Successfully set this.channel to the newly created group channel.");
    } catch (error) {
      console.error("Error creating group channel:", error);
    }
  };

  sendMessage = async (textMessage, customType = '', data = '') => {
    if (!this.channel) {
      console.error('Channel is not defined.');
      return;
    }

    const params = {
      message: textMessage,
      customType: customType,
      data: data,
    };

    try {
      this.channel.sendUserMessage(params)
        .onSucceeded((message) => {
          const formattedMessage = {
            messageId: message.messageId,
            text: message.message,
            senderId: message.sender.userId,
            createdAt: message.createdAt,
            nickname: message.sender.nickname,
          };
          this.setState(prevState => ({
            messages: [...prevState.messages, formattedMessage]
          }));
        })
        .onFailed((error, message) => {
          console.error("Failed to send message:", error);
        });
    } catch (error) {
      console.error("Send Message Error:", error);
    }
  };

  renderChannelTypeSelection() {
    return (
      <div>
        <button onClick={() => this.setState({ channelType: 'open' }, this.createOpenChannel)}>Open Channel</button>
        <button onClick={() => this.setState({ channelType: 'group' }, this.createGroupChannel)}>Group Channel</button>
      </div>
    );
  }

  // Method to initialize and fetch messages for a channel
  initializeMessageCollection = async (channel) => {
    const filter = new MessageFilter();
    const limit = 100;
    const startingPoint = Date.now();
    const messageCollection = channel.createMessageCollection({
      filter,
      limit,
      startingPoint,
    });
    await messageCollection.initialize(MessageCollectionInitPolicy.CACHE_AND_REPLACE_BY_API);
    this.setState({ messageCollection });
    // Optionally, immediately load messages upon initialization
    this.loadPreviousMessages();
  };

  // Method to load previous (older) messages
  loadPreviousMessages = async () => {
    const { messageCollection } = this.state;
    if (messageCollection && messageCollection.hasPrevious) {
      const messages = await messageCollection.loadPrevious();
      this.setState(prevState => ({
        messages: [...messages, ...prevState.messages],
      }));
    }
  };

  // Method to load next (newer) messages
  loadNextMessages = async () => {
    const { messageCollection } = this.state;
    if (messageCollection && messageCollection.hasNext) {
      const messages = await messageCollection.loadNext();
      this.setState(prevState => ({
        messages: [...prevState.messages, ...messages],
      }));
    }
  }; 



  render() {
    const { isConnected, channelType, messages, userId } = this.state;
    return (
      <div className="app">
        <Title />
        {!isConnected && (
          <div>
            <input
              type="text"
              placeholder="Enter user ID"
              value={userId}
              onChange={this.handleUserIdChange}
            />
            <button onClick={this.handleLogin}>Login</button>
          </div>
        )}
        {isConnected && !channelType && this.renderChannelTypeSelection()}
        {isConnected && channelType && (
          <>
            <MessageList messages={messages} />
            <SendMessageForm sendMessage={this.sendMessage} />
          </>
        )}
      </div>
    );
  }
}

export default App;
